// 1
let initialState = {
    balls : 5
}
// 2
function BallReducer(state = initialState , action){
    // update
        // we can perfrom n type of changes

    // state change logic
    switch (action.type) {
        case "buy_balls":
            // these cases override state
                // 1st way
            let newState = {
                balls: state.balls-1
            }
            return newState;
            
        case "sell_balls":
            // 2nd way
            return {balls: state.balls+1}
        default:
            return state;
    }

}
export default BallReducer;
