import React from 'react';
import {connect} from "react-redux";
function Cart(props) {
    let cart = props.cart;
    // const handleValue = (e) => {
    //     console.log(e.target.value);
    // }
    return (
        <>
        {
            cart.length === 0? <div>Your Cart is empty</div>:
            cart.map((cartObj)=>{
                return (
                    <div key={cartObj.id}>
                        <img src={cartObj.image} style={{height:"10rem"}} alt="" />
                        <div>{cartObj.title}</div>
                        <div>{cartObj.price}</div>
                        <div>{cartObj.description}</div>
                        <input type="number"  min="1" value={cartObj.count} onChange={props.addItemToCart}/>
                    </div>
                )
            })
        }
        </>
    )
}
const mapStateToProps = (store)  => {
    return store;
}
const mapDispatchToProps = (dispatch) => {
    return {
      addItemToCart: (item) => dispatch({type: "AddToCart", payload: item})
    }
  }
export default connect(mapStateToProps, mapDispatchToProps)(Cart);
