import { combineReducers } from "redux";
import Batreducer from "./redux/bat/Batreducer";
import BallReducer from "./redux/ball/allreducer";
// merged store 
const rootReducer = combineReducers({
    Ball: BallReducer,
    Bat: Batreducer
})

export default rootReducer;
