import {createStore } from "redux";
// 1
let initialState = {
    balls : 5
}
// 2
function BallReducer(state = initialState , action){
    // update
        // we can perfrom n type of changes
    switch (action.type) {
        case "buy_balls":
            return {balls: state.balls-1}
        default:
            break;
    }

}

// 3
const store = createStore(BallReducer);

export default store;
