let inititalState = {
    quantity: 20
}
function Batreducer(state = inititalState, action) {
    switch (action.type) {
        case "buy_bat":
            return {quantity: state.quantity - action.payLoad};
        default:
            return state;
    }
}

export default Batreducer;
