import React from 'react';
import {connect} from "react-redux";

function Balls(props) {
    console.log(props);
    // 6
    return (
        <div>
            <h1>Number of Balls : {props.balls}</h1>
            <button onClick={props.buyballs}>Buy Balls</button>  
        </div>
    )
}

// 5 -> provide state variables from store
const mapStateToProps = store => {
    console.log("In map state to prop", store);
    return store;
}
const maptoDispatchProps = dispacth => {
    return{

        buyballs : () => {
            return dispacth({type: "buy_balls"});
        }
    }
}
// higher order function
export default connect(mapStateToProps, maptoDispatchProps)(Balls);
