import { products } from "../data/data";
import {createStore} from "redux";
let initialState = {
    products,
    cart: [],
    cartCount: 0,
    currItem : null
}
function cartReducer(state=initialState, action){
    switch (action.type) {
        case "ViewItem": 
           return   {
            products: state.products,
            cart: state.cart,
            currItem: action.payload
        };
        
        case "AddToCart": 
            let itemId = action.payload.id;
            let count = 1;
            let newState;
            if(state.cart.length > 0){
                for(let i= 0;i <state.cart.length; i++){
                    if(state.cart[i].id == itemId){
                        state.cart[i].count += 1;
                        newState = {
                            products: state.products,
                            cart: state.cart,
                            cartCount: state.cartCount + 1,
                            currItem: state.currItem
                        }
                    }
                }
            }
            else{
                newState = {
                   products: state.products,
                   cart: [...state.cart, {...action.payload, count}],
                   cartCount: state.cartCount + 1,
                   currItem: state.currItem
               }
            }
           return newState;
        
        default:
            return state;
    }
}

const store = createStore(cartReducer);

export default store;
