import React, { useState } from 'react';
import { connect } from "react-redux";

function Bat(props) {
    const [value, setValue]  = useState("");
    return (
        <div>
            <h1>Number of Bats : {props.balls}</h1>
            <input type="number" value={value} onChange={(e)=> setValue(e.target.value)}></input> 
            <button onClick={props.buyBats(value), setValue("")}>Buy Balls</button>  
        </div>
    )
}

const mapStateToProps = store => {
    return store.Bat;
}
const maptoDispatchProps = dispatch => {
    return {
        buyBats: (val)=> {
            dispatch({
                type:"buy_bat", 
                // send data to reducer
                payload: val})
        }
    }
}

export default connect(mapStateToProps, maptoDispatchProps)(Bat);
