import {createStore } from "redux";
// 1
let state = {
    balls : 5
}
// 2
function BallReducer(initialState = state, action){
    // update
        // we can perfrom n type of changes
    console.log("In store", action);
    return initialState;
}
const store = createStore(BallReducer);

export default store;
