import { products } from "../data/data";
import {createStore} from "redux";
let initialState = {
    products,
    currItem : null
}
function cartReducer(state=initialState, action){
    switch (action.type) {
        default:
            return state;
    }
}

const store = createStore(cartReducer);

export default store;
