import React from "react";
import { connect } from "react-redux";
import {useHistory } from 'react-router'

function HomePage(props) {
  const history = useHistory();
  const handleViewItem = (product) => {
    // history.push(`/product/${product.id}`);
    console.log("this item is viewed"+product.id);
    props.viewItem(product.id);
  };
  return (
    <>
      {props.products.map((productObj) => {
        return (
          <div
            key={productObj.id}
            style={{ width: "20rem", display: "flex", flexDirection: "column" }}
          >
            <img src={productObj.image} style={{ height: "10rem" }} />
            <div>
              <h2>{productObj.title}</h2>
            </div>
            <div>
              <h3>{productObj.price}</h3>
            </div>
            <div>
              <p>{productObj.description}</p>
            </div>
            <div>
              <button onClick={() => handleViewItem(productObj)}>View Item</button>
              <button>Add to Cart</button>
            </div>
          </div>
        );
      })}
    </>
  );
}

const mapStateToProps = (store) => {
  return store;
};
const mapDispatchToProps = (dispatch) => {
  return {
    viewItem: (id) => dispatch({type:"ViewItem", payload: id})
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
